mapper.update:
update book set name = #{name},description = #{description},publish_date = #{publishDate},author = #{author},
publisher = #{publisher},book_no = #{bookNo},cover = #{cover},categoryid = #{categoryid},score = #{score},nums = #{nums},updatetime = #{updatetime}
where id = #{id}

mapper.select:
select b.*,c.name as categoryName from book b left join category c
on b.categoryid = c.id
where b.id = #{id}
